import React from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import TeamScoreBox from './TeamScoreBox';
import EventBox from './EventBox';
import AdditionalInfoBox from './AdditionalInfoBox';

const ScoreboardRow = ({ team1, team2, currentEvent, additionalInfo }) => {
  // Add the Stars component
  const Stars = () => {
    const stars = 108;
    const columns = 18;
    const [glowingStars, setGlowingStars] = React.useState([]);
    const highlightedStars = React.useRef([]);

    React.useEffect(() => {
      const interval = setInterval(() => {
        highlightedStars.current = Array.from({ length: 5 }, () =>
          Math.floor(Math.random() * stars)
        );
        setGlowingStars([...highlightedStars.current]);
      }, 3000);

      return () => clearInterval(interval);
    }, []);

    return (
      <div
        className="absolute inset-0 z-0"
        style={{
          display: "grid",
          gridTemplateColumns: `repeat(${columns}, 1fr)`,
          gap: `1px`,
        }}
      >
        {[...Array(stars)].map((_, starIdx) => {
          const isGlowing = glowingStars.includes(starIdx);
          const delay = (starIdx % 10) * 0.1;
          return (
            <div
              key={`star-${starIdx}`}
              className="relative flex items-center justify-center"
            >
              <motion.div
                initial={{ scale: 1 }}
                animate={{
                  scale: isGlowing ? [1, 1.2, 2.5, 2.2, 1.5] : 1,
                  background: isGlowing ? "#fff" : "#666",
                }}
                transition={{
                  duration: 2,
                  ease: "easeInOut",
                  delay: delay,
                }}
                className="bg-[#666] h-[1px] w-[1px] rounded-full relative z-20"
              />
              <AnimatePresence mode="wait">
                {isGlowing && (
                  <motion.div
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    exit={{ opacity: 0 }}
                    transition={{
                      duration: 2,
                      ease: "easeInOut",
                      delay: delay,
                    }}
                    className="absolute left-1/2 -translate-x-1/2 z-10 h-[4px] w-[4px] 
                             rounded-full bg-blue-500 blur-[1px] shadow-2xl shadow-blue-400"
                  />
                )}
              </AnimatePresence>
            </div>
          );
        })}
      </div>
    );
  };

  return (
    <div className="flex flex-col gap-4">
      {/* Main Score Row */}
      <div className="w-full flex items-center justify-between px-8 py-4
                    animated-content-box premium-box-shadow content-box-glow
                    rounded-lg border border-blue-400/20 relative overflow-hidden">
        {/* Add Stars background */}
        <Stars />

        {/* Content overlay */}
        <div className="relative z-10 flex w-full items-center justify-between">
          {/* Left Team */}
          <TeamScoreBox 
            teamName={team1.name}
            score={team1.score}
            overs={team1.overs}
            logo={team1.logo}
          />

          {/* Middle Event */}
          <EventBox event={currentEvent} />

          {/* Right Team */}
          <TeamScoreBox 
            teamName={team2.name}
            score={team2.score}
            overs={team2.overs}
            logo={team2.logo}
            isReversed={true}
          />
        </div>
      </div>

      {/* Additional Info Box - Centered below */}
      {additionalInfo && (
        <div className="flex justify-center">
          <AdditionalInfoBox additionalInfo={additionalInfo} />
        </div>
      )}
    </div>
  );
};

export default ScoreboardRow;
