import React, { useState, useEffect } from 'react';

const SpeedBox = ({ speedData }) => {
  const [currentBall, setCurrentBall] = useState({
    runs: '0'
  });
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    if (!speedData) return;
    setIsVisible(true);

    if (Array.isArray(speedData)) {
      const latestBall = speedData[0];
      if (latestBall) {
        setCurrentBall({
          runs: latestBall.Runs || '0'
        });
      }
    } 
    else if (typeof speedData === 'object') {
      setCurrentBall({
        runs: speedData.Runs || '0'
      });
    }

    const timer = setTimeout(() => {
      setIsVisible(false);
    }, 5000);

    return () => clearTimeout(timer);
  }, [speedData]);

  if (!isVisible) return null;

  return (
    <div className="fixed inset-0 z-50 pointer-events-none">
      {/* Semi-transparent backdrop just for ThisOverBox area */}
      <div className="absolute right-[1.5rem] top-[240px] w-[180px] h-[120px]
                      bg-[#0d1631]/80 backdrop-blur-sm rounded-lg
                      transition-opacity duration-300" />

      {/* Speed display */}
      <div className="absolute right-[2.5rem] top-[270px]
                      bg-gradient-to-br from-[#1a2442] via-[#2a3454] to-[#1a2442]
                      backdrop-blur-xl backdrop-saturate-150
                      border border-blue-400/20
                      shadow-[rgba(0,0,0,0.56)_0px_22px_70px_4px]
                      rounded-xl
                      transition-all duration-500 ease-in-out
                      animate-slideIn">
        <div className="flex items-center justify-between py-[0.7rem] px-4">
          <span className="text-[1.6rem] leading-[1.5rem] font-normal text-[#e5e5e5]">
            {currentBall.runs}r
          </span>
        </div>
      </div>
    </div>
  );
};

export default SpeedBox;
