import React, { useState, useEffect } from 'react';

const AdditionalInfoBox = ({ additionalInfo }) => {
  const [glowIntensity, setGlowIntensity] = useState(0);

  // Animate glow when info changes
  useEffect(() => {
    if (additionalInfo) {
      setGlowIntensity(1);
      const timer = setTimeout(() => setGlowIntensity(0), 2000);
      return () => clearTimeout(timer);
    }
  }, [additionalInfo]);

  if (!additionalInfo) return null;

  return (
    <div className="relative px-6 py-2 rounded-lg 
                    border border-blue-400/20 
                    shadow-[0_0_15px_rgba(59,130,246,0.15)]
                    backdrop-blur-lg
                    transition-all duration-700
                    hover:shadow-[0_0_20px_rgba(59,130,246,0.25)]
                    group">
      {/* Background glow effect */}
      <div 
        className="absolute inset-0 blur-xl transition-opacity duration-700"
        style={{ 
          background: 'radial-gradient(circle, rgba(59,130,246,0.2) 0%, transparent 70%)',
          opacity: glowIntensity * 0.3,
        }}
      />

      {/* Animated border gradient */}
      <div className="absolute inset-0 rounded-lg bg-gradient-to-r from-blue-500/0 via-blue-500/20 to-blue-500/0
                      animate-[shine_3s_ease-in-out_infinite]
                      opacity-0 group-hover:opacity-100 transition-opacity duration-700" />

      {/* Content with glow */}
      <span className="relative text-2xl font-semibold text-center leading-tight whitespace-nowrap
                     text-[#e5e5e5] leading-none z-10
                     drop-shadow-[0_2px_3px_rgba(0,0,0,0.3)]
                     transition-all duration-300
                     group-hover:text-blue-100">
        {additionalInfo}
      </span>

      {/* Ambient light effect */}
      <div className="absolute inset-0 rounded-lg
                      bg-[radial-gradient(circle_at_50%_0%,rgba(59,130,246,0.15),transparent_70%)]
                      opacity-0 group-hover:opacity-100 transition-opacity duration-700" />

      <style jsx>{`
        @keyframes shine {
          0% { transform: translateX(-100%); }
          50% { transform: translateX(100%); }
          100% { transform: translateX(-100%); }
        }

        @keyframes pulse {
          0% { opacity: 0.2; }
          50% { opacity: 0.4; }
          100% { opacity: 0.2; }
        }
      `}</style>
    </div>
  );
};

export default AdditionalInfoBox;
